			AND (elayers.wod05_osd_all.latitude >= ((lat - buffer))))
			AND (elayers.wod05_osd_all.latitude <= ((lat + buffer))))
			AND (elayers.wod05_osd_all.depth >= (res1))) -- top depth
			AND (elayers.wod05_osd_all.depth <= (res2))) --lower depth
			)
			AS foo;

